    data () {
      return {
        currentItem: '',
        distance: -9999,
        nvaTopStyle: {
          visibility: 'hidden'
        },
        loader: false,
        contentHeight: window.innerHeight
      }
    },
    methods: {
      callBack (event) {
        let menu

        menu = event.target
        if (event.target.nodeName.toUpperCase() === 'P') {
          menu = event.target.parentElement
        }
        menu.style.transform = 'translate(-100%, 0)'
      },
      /**
       * 切换Item
      **/
      activeItem (event) {
        // this.fixedOnTop = false
        // 首次点击Nva取消置顶
        if (this.currentItem === '') {
          this.currentItem = event.target
          this.fixedOnTop = false
          this.nvaTopStyle.visibility = 'visible'
        }
        if (this.currentItem !== event.target) {
          this.currentItem.className = ''
          // if ()
          // 2s后隐藏loader
          this.loader = true
          window.setTimeout(() => {
            this.loader = false 
          }, 1000)
        }
        event.target.className = 'active'
        this.currentItem = event.target
      }
    },
    computed: {
      fixedOnTop () {
        if (this.currentItem === '') {
          return true
        }
        if (this.distance > -1) {
          document.querySelector('#J_tabClone').innerHTML = this.currentItem.innerHTML
          return true
        }
        return false
      },
      tabConData () {
        // 每次点击Item会清除顶端样式
        document.querySelector('#J_tabClone').className = 'tab-clone top'
        if (this.currentItem === '') {
          return this.tabRsponseData[0]
        }
        // 常规情况下返回 (this.currentItem.attributes[1].value)
        // 为了少造点数据、就循环利用了
        return this.tabRsponseData[(this.currentItem.attributes[1].value)%6]
      }
    },
    // 组件初始化的钩子
    mounted () {
      this.$nextTick(() => {
        let nvaEle = document.querySelector('#J_tabNav')
        // 左侧nva滚动监听
        nvaEle.addEventListener('scroll', () => { this.distance = event.target.scrollTop - this.currentItem.offsetTop })
      })
    },
    ready () {

    }
  }
